import React from 'react';


//1.1. функция открытиия попапа, кот. придет из компонента main (здесь отрисовывается карточка)
//1.2. данные карточки, кот. мы передаем при отрисовке
//внутри этого класса только разметка + вызов функции при клике на картинку
function Card(props) {
  
  function handleClick() {
    props.onCardClick(props.card);
  } 

  return (
    <div className="Card">
      <div className="element-template">
        <div className="element">
          <button type="button" className="element__delete" />
          <img src={props.card.link} alt={props.card.name} className="element__image" onClick={() => handleClick(props.card)}/>
          <div className="element__title">
            <h2 className="element__name">{props.card.name}</h2>
            <div className="element__like-container">
              <button type="button" className="element__like" />
              <span className="element__like-count" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
  
export default Card;
