import React from 'react';
import api from '../utils/api';
import {useState, useEffect, useContext} from 'react';
import Card from './Card';
import { CurrentUserContext } from '../contexts/CurrentUserContext';


//добавьте функцию handleCardDelete в Main

function Main(props) {
  const currentUser = useContext(CurrentUserContext);

  const [userName, setUserName] = useState('Жак-Ив Кусто');
  const [userDescription, setUserDescription] = useState('Исследователь океана');
  const [userAvatar, setUserAvatar] = useState('');
  const [cards, setCards] =  useState([]);
  

  useEffect(() => {
    api.getProfile()
      .then((data) => {
        setUserName(data.name);
        setUserDescription(data.about);
        setUserAvatar(data.avatar);
      })
      .catch((err) => {
        console.error(err);
      })
 
    api.getInitialCards()
      .then((res) => setCards(res))
      .catch((err) => {
        console.error(err);
      })

  }, [])

  
  //Клик на лайк
  
  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    
    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  }


    return (
    <div className="Main">
      <main>
          <section className="profile">
            <div className="profile__avatar-edit">
              <img
                src={userAvatar}
                alt="Фото профиля"
                className="profile__avatar-foto"
              />
              <button className="profile__avatar-foto-edit" onClick={props.onEditAvatar} />
            </div>
            <div className="profile__info">
              <div className="profile__title">
                <h1 className="profile__info-title">{userName}</h1>
                <button type="button" className="profile__edit" onClick={props.onEditProfile}/>
              </div>
              <p className="profile__info-subtitle">{userDescription}</p>
            </div>
            <button type="button" className="profile__add" onClick={props.onAddPlace}></button>
          </section>
          
          <section className="elements" >
            {
              cards.map((card) => (
                <Card
                  key={card._id}
                  card={card}
                  name={card.name}
                  link={card.link}
                  onCardClick={props.onCardClick}
                  // onCardCLike={onCardCLike}
                  // onCardDelete={onCardDelete}

                />
              ))
            }
          </section>
        </main>
    </div>
  );
}
  
export default Main;
