import React from 'react';
import {useState, useEffect, useContext} from 'react';
import '../utils/api';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import EditProfilePopup from './EditProfilePopup';
import ImagePopup from './ImagePopup';
import Card from './Card';

import { CurrentUserContext} from '../contexts/CurrentUserContext';


function App() {
  // Стейты для попапов (принимает состояние открыт(true), закрыт (False))
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);

  // Стейт для карточек
  const [isSelectedCard, setSelectedCard] = React.useState(null);
  // Стейт 
  const [cards, setCards] = React.useState([]);

 // Стейт для карточек (вызывает api.getProfile() и обновляет стейт-переменную из полученного значения)
  const [currentUser, setCurrentUser] = React.useState({
    name: 'testName',
    about: 'testDescription',
    avatar: 'tesrAvatar'
  });

  //Создать обработчик handleUpdateUser. Внутри этого обработчика вызовите api.getProfile
  //добавьте handleUpdateAvatar, вызывающий api.setUserAvatar

  useEffect(() => {
    Promise.all([api.getProfile(), api.getInitialCards()])
    .then(([user, cards]) => {
      setCurrentUser(user);
      setCards(cards);
    })
    .catch((err) => {
      console.error(err);
    });
  }, []);

  function handleUpdateUser(data) {
    api.updateUserInfo(data)
    .then((newUser) => {
      setCurrentUser(newUser);
      closeAllPopups();
    })
    .catch((err) => {
      console.error(err);
    });
  }


  // useEffect(() => {
  //   api.getProfile()
  //     .then((data) => {
  //       setUserName(data.name);
  //       setUserDescription(data.about);
  //       setUserAvatar(data.avatar);
  //     })
  //     .catch((err) => {
  //       console.error(err);
  //     })
  // }, [])

  // Открытие попапа редактирования аватара
  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  // Открытие попапа редактирования профиля
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  // Открытие попапа добавления места
  function  handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  // Закрытие попапа
  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null);
  }


  // Обработчик клика по изображению
  function handleCardClick(card) {
    setSelectedCard(card);
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <div className="page">
          <div className="content">
            <Header />
            <Main 
              onEditAvatar={handleEditAvatarClick} 
              onEditProfile={handleEditProfileClick} 
              onAddPlace={handleAddPlaceClick}
              onCardClick={handleCardClick}
            />
            <Footer />

            <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} />
            {/* <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} /> */}

            <PopupWithForm
              name={'avatar'}
              title={'Обновить автар'}
              buttonText={'Сохранить'}
              isOpen={isEditAvatarPopupOpen}
              onClose={closeAllPopups}
            >
              <input
                  className="popup__input popup__input_name_add-link"
                  id="img-input"
                  type="url"
                  minLength={1}
                  name="link"
                  required=""
                  placeholder="Ссылка на картинку"
                  defaultValue=""
                />
              <span
                  id="error-img-input"
                  className="popup__error popup__error_visible"
                />
            </PopupWithForm>

            <PopupWithForm
              name='add-element'
              title='Новое место'
              buttonText={'Создать'}
              isOpen={isAddPlacePopupOpen}
              onClose={closeAllPopups}
            >
              <input
                  id="name-input"
                  type="text"
                  name="name"
                  required=""
                  minLength={2}
                  maxLength={30}
                  className="popup__input popup__input_name_add-name"
                  placeholder="Название"
                  defaultValue=""
                />
                <span
                  id="error-name-input"
                  className="popup__error popup__error_visible"
                />
                <input
                  id="link-input"
                  type="url"
                  minLength={1}
                  name="link"
                  required=""
                  className="popup__input popup__input_name_add-link"
                  placeholder="Ссылка на картинку"
                  defaultValue=""
                />
                <span
                  id="error-link-input"
                  className="popup__error popup__error_visible"
                />
            </PopupWithForm>
            <ImagePopup
              card={isSelectedCard}
              onClose={closeAllPopups}
            />
          </div>
        </div>
      </div>
    </CurrentUserContext.Provider>

  );
}

export default App;
