import React from 'react';
import '../index.css';
// import Card from './Card';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';

function App() {
  // const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  // const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  // const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);

  // handleEditAvatarClick = () => {
  //   const avatarEditModal = document.querySelector('.popup_type_avatar');
  //   avatarEditModal.classList.add('popup_opened');
  // }

  // handleEditProfileClick = () => {
  //   const profileEditModal =  document.querySelector('.popup_type_edit');
  //   profileEditModal.classList.add('popup_opened');
  // }

  // handleAddPlaceClick = () => {
  //   const cardAddModal = document.querySelector('.popup_type_add-element');
  //   cardAddModal.classList.add('popup_opened');
  // }

  return (
    <div className="App">
      <div className="page">
        <div className="content">
          <Header />
          <Main />
          {/* <Main onEditAvatar={handleEditAvatarClick} /> */}
          {/* <Main 
                onEditAvatar={handleEditAvatarClick} 
                onEditProfile={handleEditProfileClick} 
                onAddPlace={handleAddPlaceClick}/> */}
          <Footer />
        </div>

        <section className="popup popup_type_edit">
          <div className="popup__container">
            <button type="button" className="popup__close" />
            <form name="popup" className="popup__form" noValidate="">
              <h2 className="popup__title">Редактировать профиль</h2>
              <input
                id="title-input"
                type="text"
                name="title"
                required=""
                minLength={2}
                maxLength={40}
                className="popup__input popup__input_name_title"
                placeholder="Имя"
                defaultValue=""
              />
              <span
                id="error-title-input"
                className="popup__error popup__error_visible"
              />
              <input
                id="subtitle-input"
                type="text"
                name="subtitle"
                required=""
                minLength={2}
                maxLength={200}
                className="popup__input popup__input_name_subtitle"
                placeholder="Профессиональная деятельность"
                defaultValue=""
              />
              <span
                id="error-subtitle-input"
                className="popup__error popup__error_visible"
              />
              <button type="submit" className="popup__save popup__save_disabled">
                Сохранить
              </button>
            </form>
          </div>
        </section>

        <section className="popup popup_type_add-element">
          <div className="popup__container">
            <button type="button" className="popup__close" />
            <form name="popup" className="popup__form" noValidate="">
              <h2 className="popup__title">Новое место</h2>
              <input
                id="name-input"
                type="text"
                name="name"
                required=""
                minLength={2}
                maxLength={30}
                className="popup__input popup__input_name_add-name"
                placeholder="Название"
                defaultValue=""
              />
              <span
                id="error-name-input"
                className="popup__error popup__error_visible"
              />
              <input
                id="link-input"
                type="url"
                minLength={1}
                name="link"
                required=""
                className="popup__input popup__input_name_add-link"
                placeholder="Ссылка на картинку"
                defaultValue=""
              />
              <span
                id="error-link-input"
                className="popup__error popup__error_visible"
              />
              <button type="submit" className="popup__save popup__save_disabled">
                Создать
              </button>
            </form>
          </div>
        </section>

        <section className="popup popup_type_delete-confirm">
          <div className="popup__container">
            <button type="button" className="popup__close" />
            <form name="popup" className="popup__form" noValidate="">
              <h2 className="popup__title">Вы уверены?</h2>
              <button type="submit" className="popup__save">
                Да
              </button>
            </form>
          </div>
        </section>

        <section className="popup popup_type_avatar">
          <div className="popup__container">
            <button type="button" className="popup__close" />
            <form name="popup" className="popup__form" noValidate="">
              <h2 className="popup__title">Обновить аватар</h2>
              <input
                id="img-input"
                type="url"
                minLength={1}
                name="link"
                required=""
                className="popup__input popup__input_name_add-link"
                placeholder="Ссылка на картинку"
                defaultValue=""
              />
              <span
                id="error-img-input"
                className="popup__error popup__error_visible"
              />
              <button type="submit" className="popup__save">
                Сохранить
              </button>
            </form>
          </div>
        </section>

        <section className="popup popup_type_image-element">
          <div className="popup__container-image">
            <button type="button" className="popup__close" />
            <figure className="popup__image">
              <img src="#" alt="#" className="popup__image-element" />
              <figcaption className="popup__caption" />
            </figure>
          </div>
        </section>

        <template className="element-template" />
      </div>
    </div>
  );
}

export default App;
