import React from 'react';
import {useState, useEffect, useContext} from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext} from '../contexts/CurrentUserContext';

function EditProfilePopup({ isOpen, onClose, onSubmit, onCloseClick, onUpdateUser }) {
  const currentUser = useContext(CurrentUserContext);

  // Стейты имени и описания пользователя
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');

  // После загрузки текущего пользователя из API,его данные будут использованы в управляемых компонентах.
  useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [isOpen, currentUser]); 


  function handleSubmit(e) {
    e.preventDefault();
    // Передаём значения управляемых компонентов во внешний обработчик
    onUpdateUser({
      name,
      about: description,
    });
  }
  
  return (
      <div className="EditProfilePopup">
        <PopupWithForm
          name={'edit'}
          title={'Редактировать профиль'}
          buttonText={'Сохранить'}
          isOpen={isOpen}
          onClose={onClose}
          onSubmit={handleSubmit}
        >
          <input
              id="title-input"
              type="text"
              name="title"
              required=""
              minLength={2}
              maxLength={40}
              className="popup__input popup__input_name_title"
              placeholder="Имя"
              defaultValue=""
            />
            <span
              id="error-title-input"
              className="popup__error popup__error_visible"
            />
            <input
              id="subtitle-input"
              type="text"
              name="subtitle"
              required=""
              minLength={2}
              maxLength={200}
              className="popup__input popup__input_name_subtitle"
              placeholder="Профессиональная деятельность"
              defaultValue=""
            />
            <span
              id="error-subtitle-input"
              className="popup__error popup__error_visible"
            />      
        </PopupWithForm>
      </div>  
    );
  }
    
export default EditProfilePopup;
