import React from 'react';
import api from '../utils/api';
import {useState, useEffect} from 'react';
import Card from './Card';


//в компоненте Main будут происходить запросы к апи
//понадобятся 4 стейта (3 для полей пользователя и один для карточек) 
//хук useEffect, чтобы загружать данные при открытии страницы (чтобы он выполнялся ровно один раз) 
//отрендерить массив карточек и не забыть про key.

function Main(props) {
  
  const [userName, setUserName] = useState('');
  const [userDescription, setUserDescription] = useState('');
  const [userAvatar, setUserAvatar] = useState();
  const [cards, setCards] =  useState([]);

  useEffect(() => {
    api.getProfile()
      .then((res) => {
        setUserName(res.name);
        setUserDescription(res.about);
        setUserAvatar(res.avatar);
      })
      .catch((err) => {
        console.error(err);
      })
 
    api.getInitialCards()
      .then((res) => setCards(res))
      .catch((err) => {
        console.error(err);
      })

  }, [])

    return (
    <div className="Main">
      <main>
          <section className="profile">
            <div className="profile__avatar-edit">
              <img
                src={userAvatar}
                alt="Фото профиля"
                className="profile__avatar-foto"
              />
              <button className="profile__avatar-foto-edit" onClick={props.onEditAvatar} />
            </div>
            <div className="profile__info">
              <div className="profile__title">
                <h1 className="profile__info-title">{userName}</h1>
                <button type="button" className="profile__edit" onClick={props.onEditProfile}/>
              </div>
              <p className="profile__info-subtitle">{userDescription}</p>
            </div>
            <button type="button" className="profile__add" onClick={props.onAddPlace}></button>
          </section>
          
          {/* <section className="elements" >
            {
              cards.map((card) => (
                <Card
                  key={card._id}
                  card={card}
                  // name={card.name}
                  // link={card.link}
                  onCardClick={props.onCardClick}
                />
              ))
            }
          </section> */}
        </main>
    </div>
  );
}
  
export default Main;
