import React from 'react';
import '../index.css';
import '../utils/api';
// import '../utils/utils';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import Card from './Card';


function App() {
  // Стейты для попапов (принимает состояние открыт(true), закрыт (False))
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);

  // Стейт для изображения
  // const [isSelectedCard, setSelectedCard] = React.useState(false);

  // Стейт для карточки, по умолчанию []
  // const[cards, setCards] =  React.useState(false);

  // Открытие попапа редактирования аватара
  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  // Открытие попапа редактирования профиля
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  // Открытие попапа добавления места
  function  handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

//   function handleEditAvatarClick() {
//     setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
// }

  // Закрытие попапа
  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    // setSelectedCard(null);
  }

  // function handleClick() {
  //   props.onCardClick(props.card);
  // } 

  // Обработчик клика по изображению
  // function handleCardClick(card) {
  //   setSelectedCard(card);
  // }

  return (
    <div className="App">
      <div className="page">
        <div className="content">
          <Header />

          <Main 
            onEditAvatar={handleEditAvatarClick} 
            onEditProfile={handleEditProfileClick} 
            onAddPlace={handleAddPlaceClick}
            // onCardClick={handleClick}
          />
          <Card  />
          <Footer />
          <ImagePopup
            onClose={closeAllPopups}
          />

          <PopupWithForm>
            name='avatar'
            title='Обновить автар'
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
          </PopupWithForm>


          <PopupWithForm
            name='avatar'
            title='Обновить автар'
            buttonText='Сохранить'
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onSubmit={handleSubmit}
          >
            <input
                className="popup__input popup__input_name_add-link"
                id="img-input"
                type="url"
                minLength={1}
                name="link"
                required=""
                placeholder="Ссылка на картинку"
                defaultValue=""
              />
          </PopupWithForm>

          <PopupWithForm>
            name='edit'
            title='Редактировать профиль'
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
          </PopupWithForm>

          <PopupWithForm>
            name='add-element'
            title='Новое место'
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
          </PopupWithForm>



        </div>

        <section className="popup popup_type_edit">
          <div className="popup__container">
            <button type="button" className="popup__close" />
            <form name="popup" className="popup__form" noValidate="">
              <h2 className="popup__title">Редактировать профиль</h2>
              <input
                id="title-input"
                type="text"
                name="title"
                required=""
                minLength={2}
                maxLength={40}
                className="popup__input popup__input_name_title"
                placeholder="Имя"
                defaultValue=""
              />
              <span
                id="error-title-input"
                className="popup__error popup__error_visible"
              />
              <input
                id="subtitle-input"
                type="text"
                name="subtitle"
                required=""
                minLength={2}
                maxLength={200}
                className="popup__input popup__input_name_subtitle"
                placeholder="Профессиональная деятельность"
                defaultValue=""
              />
              <span
                id="error-subtitle-input"
                className="popup__error popup__error_visible"
              />
              <button type="submit" className="popup__save popup__save_disabled">
                Сохранить
              </button>
            </form>
          </div>
        </section>

        <section className="popup popup_type_add-element">
          <div className="popup__container">
            <button type="button" className="popup__close" />
            <form name="popup" className="popup__form" noValidate="">
              <h2 className="popup__title">Новое место</h2>
              <input
                id="name-input"
                type="text"
                name="name"
                required=""
                minLength={2}
                maxLength={30}
                className="popup__input popup__input_name_add-name"
                placeholder="Название"
                defaultValue=""
              />
              <span
                id="error-name-input"
                className="popup__error popup__error_visible"
              />
              <input
                id="link-input"
                type="url"
                minLength={1}
                name="link"
                required=""
                className="popup__input popup__input_name_add-link"
                placeholder="Ссылка на картинку"
                defaultValue=""
              />
              <span
                id="error-link-input"
                className="popup__error popup__error_visible"
              />
              <button type="submit" className="popup__save popup__save_disabled">
                Создать
              </button>
            </form>
          </div>
        </section>

        <section className="popup popup_type_delete-confirm">
          <div className="popup__container">
            <button type="button" className="popup__close" />
            <form name="popup" className="popup__form" noValidate="">
              <h2 className="popup__title">Вы уверены?</h2>
              <button type="submit" className="popup__save">
                Да
              </button>
            </form>
          </div>
        </section>

        {/* <section className="popup popup_type_avatar">
          <div className="popup__container">
            <button type="button" className="popup__close" />
            <form name="popup" className="popup__form" noValidate="">
              <h2 className="popup__title">Обновить аватар</h2>
              <input
                id="img-input"
                type="url"
                minLength={1}
                name="link"
                required=""
                className="popup__input popup__input_name_add-link"
                placeholder="Ссылка на картинку"
                defaultValue=""
              />
              <span
                id="error-img-input"
                className="popup__error popup__error_visible"
              />
              <button type="submit" className="popup__save">
                Сохранить
              </button>
            </form>
          </div>
        </section> */}

        <section className="popup popup_type_image-element">
          <div className="popup__container-image">
            <button type="button" className="popup__close" />
            <figure className="popup__image">
              <img src="#" alt="#" className="popup__image-element" />
              <figcaption className="popup__caption" />
            </figure>
          </div>
        </section>





      </div>
    </div>
  );
}

export default App;
