function ImagePopup({name, isOpen}) {
    return (
      <div className={`popup popup_type_${name} ${isOpen ? `popup_opened`: ""}`}>
        <div className="popup__container-image">
            <button type="button" className="popup__close" onClick={onClose}/>
            <figure className="popup__image">
              <img src="#" alt="#" className="popup__image-element" />
              <figcaption className="popup__caption" />
            </figure>
          </div>
      </div>
    );
}
  
export default ImagePopup;

//этот компонент принимает только два props (ссылка на функцию закрытия и данные карточки) и использовать их в разметке. 
//Чтобы не падали ошибки, когда никакая карточка не выбрана, 
//посмотрите в сторону использования тернарных операторов. 
