import React from 'react';
import {useState, useEffect, useContext} from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';


//добавить пропс onCardLike для компонента Card и задать в него эту функцию. 
//Также добавьте в Card обработчик клика handleLikeClick и вызовите из него onCardLike с аргументом card — по аналогии с уже имеющимся обработчиком handleClick.
//добавьте пропс onCardDelete и обработчик handleDeleteClick в Card
//После запроса в API, обновите стейт cards с помощью метода filter: создайте копию массива, исключив из него удалённую карточку.

function Card({onCardClick, card, onCardLike, onCardDelete}) {
  //Подписка на контекст
  const currentUser = useContext(CurrentUserContext);
  
  // Определяем, являемся ли мы владельцем текущей карточки (должна ли в текущей карточке показываться иконка удаления)
  const isOwn = card.owner._id === currentUser._id;
  // Создаём переменную, которую после зададим в `className` для кнопки удаления
  const cardDeleteButtonClassName = (
    `element__delete ${isOwn ? 'element__delete_visible' : 'element__delete_hidden'}`
  );

  // Определяем, есть ли у карточки лайк, поставленный текущим пользователем
  const isLiked = card.likes.some(i => i._id === currentUser._id); 
  // Создаём переменную, которую после зададим в `className` для кнопки лайка
  const cardLikeButtonClassName = (
    `element__like ${isLiked ? 'element__like_active' : ''}`
  );

  function handleClick() {
    props.onCardClick(props.card);
  } 

  return (
    <div className="Card">
      <div className="element-template">
        <div className="element" key={props._id}>
          <button type="button" className="element__delete" />
          <img className="element__image" src={props.link} alt={props.name}  onClick={handleClick}/>
          <div className="element__title">
            <h2 className="element__name">{props.name}</h2>
            <div className="element__like-container">
              <button type="button" className="element__like" />
              <span className="element__like-count" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
  
export default Card;
