import React from 'react';
import api from '../utils/api';
import {useState, useEffect, useContext} from 'react';
import Card from './Card';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Main({onEditAvatar, onEditProfile, onAddPlace, onCardClick, onCardLike, onCardDelete}) {
  const currentUser = useContext(CurrentUserContext);
  const [userName, setUserName] = useState('Жак-Ив Кусто');
  const [userDescription, setUserDescription] = useState('Исследователь океана');
  const [userAvatar, setUserAvatar] = useState('');

  const [userInfo, setUserInfo] = useState({})
  const [cards, setCards] =  useState([]);
   
  // useEffect(() => {
  //   api.getProfile()
  //     .then((data) => {
  //       setUserName(data.name);
  //       setUserDescription(data.about);
  //       setUserAvatar(data.avatar);
  //     })
  //     .catch((err) => {
  //       console.error(err);
  //     })
 
  //   api.getInitialCards()
  //     .then((res) => setCards(res))
  //     .catch((err) => {
  //       console.error(err);
  //     })

  // }, [])

  useEffect(() => {
    api.getProfile()
      .then((data) => {
        setUserInfo(data);
      })
      .catch((err) => {
        console.error(err);
      })
 
    api.getInitialCards()
      .then((res) => setCards(res))
      .catch((err) => {
        console.error(err);
      })

  }, [])



  return (
    <div className="Main">
      <main>
          <section className="profile">
            <div className="profile__avatar-edit">
              <img
                src={currentUser.avatar}
                alt="Фото профиля"
                className="profile__avatar-foto"
              />
              <button className="profile__avatar-foto-edit" onClick={onEditAvatar} />
            </div>
            <div className="profile__info">
              <div className="profile__title">
                <h1 className="profile__info-title">{currentUser.name}</h1>
                <button type="button" className="profile__edit" onClick={onEditProfile}/>
              </div>
              <p className="profile__info-subtitle">{currentUser.about}</p>
            </div>
            <button type="button" className="profile__add" onClick={onAddPlace}></button>
          </section>
          
          <section className="elements" >
            {cards.map((card) => (
                <Card
                  key={card._id}
                  card={card}
                  name={card.name}
                  link={card.link}
                  onCardClick={onCardClick}
                  onCardCLike={onCardLike}
                  onCardDelete={onCardDelete}
                />
            ))}
          </section>
      </main>
    </div>
  );
}
  
export default Main;
