import React, { useEffect } from 'react';
import api from '../utils/api';
import Card from './Card';

function Main({onEditAvatar, onEditProfile, onAddPlace, userName, userDescription, userAvatar}) {
  
  const[userInfo, setUserInfo] = React.useState({})
  const[cards, setCards] =  React.useState([]);
  useEffect(() => {
    Promise.all([])
  }

  )
  // const [userName, setUserName] = React.useState('Жак-Ив Кусто');
  // const [userDescription, setUserDescription] = React.useState('Исследователь океана');
  // const [userAvatar, setUserAvatar] = React.useState();

    return (
    <div className="Main">
      <main>
          <section className="profile">
            <div className="profile__avatar-edit">
              {/* style={{ backgroundImage: `url(${userAvatar})` }} */}
              <img
                src="<%=require('./images/avatar.jpg')%>"
                alt="Фото профиля"
                className="profile__avatar-foto"
              />
              <button className="profile__avatar-foto-edit" onClick={onEditAvatar} />
            </div>
            <div className="profile__info">
              <div className="profile__title">
                <h1 className="profile__info-title">{userName}</h1>
                <button type="button" className="profile__edit" onClick={onEditProfile}/>
              </div>
              <p className="profile__info-subtitle">{userDescription}</p>
            </div>
            <button type="button" className="profile__add" onClick={onAddPlace}></button>
          </section>
          
          <section className="elements" >
            {
              cards.map(card => (
                <Card
                  card={card}
                  key={card._id}
                  link={card.link}
                  name={card.name}
                  likes={card.lokes.length}
                  onCardClick={onCardClick}
                />
              ))
            }
          </section>
        </main>
    </div>
  );
}
  
export default Main;

//Только в компоненте Main будут происходить запросы к апи
//Тут вам понадобятся 4 стейта (3 для полей пользователя и один для карточек) 
//и хук useEffect, чтобы загружать данные при открытии страницы
//внимание уделите зависимостям хука — чтобы он выполнялся ровно один раз 
//разве что надо будет еще аккуратно отрендерить массив карточек и не забыть про key.
